import { APP, BaseComponentProps } from '../../typings';
import { CustomRenderParams } from './const';
import { BasicsAttribute } from '../../utils/attribute';

export interface DynamicUser extends BaseComponentProps {
  /**
   * @description 属性对象的数组，用来生成 FormItem；可以通过模拟属性对象的数据结构，生成自定义FormItem
   * @type {BasicsAttribute}
   */
  insAttrs: any;
  instance?: APP.instanceInfoType;
  instanceLoading?: boolean;
  formRef?: any;
  oldValues?: any;
  isProcess?: any;
  suffix?: { [k: string]: any };
  isAdd?: boolean;
  btnMultiline?: string; //单双行
  readOnly?: boolean;
  hideLabel?: boolean;
  isChangeLeft?: boolean;
  isChangeRight?: boolean;
  productId?: string; // 产品，型谱用
  /** 是否是横向布局 */
  isHorizontal?: boolean;
  layoutParams?: 'leftRight' | 'topBottom';
  customRender?: Record<string, React.FC<CustomRenderParams>>;
  canRender?: () => void;
  setDefaultValue?: (props: { listCode: string; value: Record<string, any> }) => void;
  onTabsChange?: (attr: BasicsAttribute, key: string) => void;
  onTransferSubmit?: (attr: BasicsAttribute, keys: string[], tabKey: string) => void;
  onEvent?: (type: string, { attr }: { attr: BasicsAttribute; [k: string]: any }) => void;
}
