import { DynamicUser } from '../../components/PlmBasicFormItem/types';
import { BasicsAttribute } from '../../utils/attribute';

export type UseAutoWidthLabelParams = [
  params: {
    /** formProps */
    plmBasicFormItemProps: Partial<DynamicUser>;
    /** 属性列表 */
    attrs: BasicsAttribute[];
    /** 是否是横向布局 */
    isHorizontal: boolean;
    /** 必选图标宽度 */
    requiredIconWidth?: number;
    /** 添加额外宽度 */
    addExtraWidth?: number;
    /** 是否只计算一次，避免重复计算 */
    isOnce?: boolean;
  },
  /** 重复执行需要的依赖 */
  deps?: any[],
];
