export type temporaryDataActionType = 'add' | 'update' | 'delete';
export type temporaryDataAction = {
    [k in temporaryDataActionType]: any[];
};
export interface temporaryData {
    [k: string]: temporaryDataAction;
}
export default function temporaryData(): {
    temporaryData: temporaryData;
    setTemporaryData: (tab: string, data: temporaryDataAction) => void;
    clearTemporaryData: () => void;
    judgeTemporaryDataIsEmpty: () => boolean;
    savePrompt: () => boolean | undefined;
};
